mapper.sql:
from base_comfort_index t join geography_station_info b ON t.stationNo = b.device_id where ${condition} and
DATE_FORMAT(t.ObserverTime,'%m%d') between #{sT} and #{eT} and t.ObserverTime between #{startDate} and #{endDate} and
t.comfort_type = #{comfortType}

from
base_comfort_index t
join geography_station_info  b ON t.stationNo = b.device_id where ${condition}
and
DATE_FORMAT(t.ObserverTime,'%m%d')  between '0101' and #{eT} and
t.comfort_type = #{comfortType}

from
base_comfort_index t
join geography_station_info  b ON t.stationNo = b.device_id where ${condition}
and
DATE_FORMAT(t.ObserverTime,'%m%d')  between #{sT} and '1231' and
t.comfort_type = #{comfortType}
